function cursorLight(e){
	const newX = e.clientX / window.innerWidth;
	console.log(e.clientX);
	console.log(e.clientY);
	document.body.style.background = "background: radial-gradient(145.00px at 40% 50%, rgba(196, 196, 196, 0) 0%, #000000 100%), linear-gradient(149.35deg, #00FFA3 24.09%, #00BD79 40.54%, #FF4D00 88.23%);"
}
document.body.addEventListener('mousemove', cursorLight)
